-- Update organization logos with custom SVG data URLs
-- Run this in your Supabase SQL Editor

-- Toolbank Logo
UPDATE organizations SET
  logo_url = 'data:image/svg+xml;base64,',
  logo_alt_text = 'Toolbank Logo'
WHERE name = 'Toolbank';

-- NMBS Logo
UPDATE organizations SET
  logo_url = 'data:image/svg+xml;base64,',
  logo_alt_text = 'NMBS Logo'
WHERE name = 'NMBS';

-- BMN Logo
UPDATE organizations SET
  logo_url = 'data:image/svg+xml;base64,',
  logo_alt_text = 'BMN Logo'  
WHERE name = 'BMN';

-- BMF Logo
UPDATE organizations SET
  logo_url = 'data:image/svg+xml;base64,',
  logo_alt_text = 'BMF Logo'
WHERE name = 'BMF';

-- Verify the updates
SELECT name, 
       CASE WHEN logo_url IS NOT NULL THEN 'Logo Added' ELSE 'No Logo' END as logo_status,
       logo_alt_text
FROM organizations 
WHERE name IN ('Toolbank', 'NMBS', 'BMN', 'BMF')
ORDER BY name;